import React, { useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import "./Artifacts.css";

const Artifacts = () => {
  const [selectedArtifact, setSelectedArtifact] = useState("bridalboss");

  const descriptions = {
    bridalboss: "description1",
    key: "description2",
    musketball: "description3",
    fort: "description4",
  };

  return (
    <div className="artifacts-page">
      <Header />
      <main>
        <h1>ArchaeoVault PROTOTYPE</h1>
        <div id="infoSection">
          <label htmlFor="modelSelect">Select Artifact:</label>
          <select
            id="modelSelect"
            value={selectedArtifact}
            onChange={(e) => setSelectedArtifact(e.target.value)}
          >
            <option value="bridalboss">Bridal Boss</option>
            <option value="key">Key</option>
            <option value="musketball">Musket Ball</option>
            <option value="fort">Fort</option>
          </select>
          <p id="modelDescription">{descriptions[selectedArtifact]}</p>
        </div>
        <div id="container3D">
        </div>
      </main>
      <Footer />
    </div>
  );
};

export default Artifacts;
